```cpp
#include<iostream>
#include<cmath>

using namespace std;

void isPrime(int n){
    if(n<=1){
        return 0;
    }
    for(int i=2;i<=sqrt(n);i++){
        if(n%i==0){
            return 0;
        }
    }
    return 1;
}

int nextPrime(int n){
    int i=n+1;
    while(true){
        if(isPrime(i)){
            return i;
        }
        i++;
    }
}

int main(){
    int input;
    cout<<"Enter the number: ";
    cin>>input;
    if(input<2){
        cout<<"Input should be greater than 1"<<endl;
        return 0;
    }
    if(nextPrime(input)<=input){
        cout<<"No next larger prime found"<<endl;
        return 0;
    }
    cout<<"The next larger prime number after "<<input<<" is "<<nextPrime(input)<<endl;
    return 0;
}
```

