```cpp
#include <iostream>
#include <string>
#include <algorithm>

void reverseString(std::string& str)
{
    int start = 0;
    int end = str.length() - 1;

    while (start < end)
    {
        std::swap(str[start], str[end]);
        ++start;
        --end;
    }
}

int main()
{
    std::string inputStr;
    std::cout << "Enter a string (with special characters, spaces, or punctuation marks): ";
    std::getline(std::cin, inputStr);

    reverseString(inputStr);

    std::cout << "Reversed string: " << inputStr << std::endl;

    return 0;
}
```

