import java.util.HashSet;
import java.util.Set;

class Solution {
    public int solution(int[] A) {
        Set<Integer> set = new HashSet<>();

        for (int num : A) {
            if (num > 0) {
                set.add(num);
            }
        }

        for (int i = 1; i <= 100001; i++) {
            if (!set.contains(i)) {
                return i;
            }
        }

        return -1;
    }
}
