type Calendar = calendar_v3.Schema$Calendar;
type CalendarEvent = calendar_v3.Schema$Event;

async function listCalendars(calendarAPI: CalendarAPI, auth: GoogleAuth): Promise<Calendar[]> {
  const { data } = await calendarAPI.calendarList.list();
  return data.items!;
}

async function listEvents(calendarAPI: CalendarAPI, calendar: Calendar): Promise<CalendarEvent[]> {
  const { data } = await calendarAPI.events.list({ calendarId: calendar.id! });
  return data.items!;
}

async function blockSlot(
  calendarAPI: CalendarAPI,
  targetCalendar: Calendar,
  eventName: string,
  start: string,
  end: string
) {
